import React, { useState } from 'react';
import './UploadPage.css'; // Import the CSS for styling
import Navbar from './Navbar';

const UploadPage = () => {
  const [formData, setFormData] = useState({
    accountNumber: '',
    userName: '',
    email: '',
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleImageChange = (e) => {
    // Handle image change and store the image file in state
    setFormData({
      ...formData,
      image: e.target.files[0], // Store the actual file, not the object URL
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { accountNumber, userName, email, image } = formData;

    // Create a FormData object to send the data as multipart/form-data
    const form = new FormData();
    form.append('accountNumber', accountNumber);
    form.append('userName', userName);
    form.append('email', email);
    if (image) form.append('image', image); // Append the image file

    try {
      const response = await fetch('http://localhost:5000/api/account/add-account', {
        method: 'POST',
        body: form,
      });

      const data = await response.json();
      if (response.ok) {
        alert('Account added successfully!');
      } else {
        alert('Failed to add account: ' + data.message);
      }
    } catch (error) {
      alert('Error uploading data: ' + error.message);
    }
  };

  return (
    <div>
      <Navbar /> {/* Include the Navbar */}
      <div className="upload-page">
        <div className="form-container">
          <h2>Upload Image and Details</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="accountNumber">Account Number:</label>
              <input
                type="text"
                id="accountNumber"
                name="accountNumber"
                value={formData.accountNumber}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="userName">User Name:</label>
              <input
                type="text"
                id="userName"
                name="userName"
                value={formData.userName}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input
                type="text"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="image">Upload Image:</label>
              <input
                type="file"
                id="image"
                name="image"
                onChange={handleImageChange}
                accept="image/*"
                required
              />
            </div>

            {formData.image && (
              <div className="image-preview">
                <img
                  src={URL.createObjectURL(formData.image)} // Image preview
                  alt="Preview"
                  className="image-preview-img"
                />
              </div>
            )}

            <button type="submit" className="submit-btn">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default UploadPage;
