import React, { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import "./Login.css"; // Ensure the CSS file is created

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [captcha, setCaptcha] = useState("");
  const [message, setMessage] = useState("");

  // Dummy captcha verification (You can replace this with a real captcha in the future)
  const handleSubmit = (e) => {
    e.preventDefault();

    // Dummy login validation (replace this with real validation)
    if (username === "" || password === "") {
      setMessage("Please enter both username and password.");
      return;
    }

    if (captcha !== "1234") {
      setMessage("Captcha is incorrect!");
      return;
    }

    // If login is successful
    setMessage("Login successful!");

    // Navigate to the Verify page after 1.5 seconds (to show success message)
    setTimeout(() => {
      navigate("/verify");
    }, 1500);
  };

  return (
    <div className="login-container">
      <motion.div
        className="hero-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <motion.h1
          className="headline"
          initial={{ y: -100 }}
          animate={{ y: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Login to Signify
        </motion.h1>
        <motion.p
          className="subheadline"
          initial={{ x: 200 }}
          animate={{ x: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Secure Signature Verification System
        </motion.p>

        <form className="login-form" onSubmit={handleSubmit}>
          <motion.input
            type="text"
            placeholder="Username"
            className="input-field"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            initial={{ x: 200 }}
            animate={{ x: 0 }}
            transition={{ type: "spring", stiffness: 120 }}
          />
          <motion.input
            type="password"
            placeholder="Password"
            className="input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            initial={{ x: 200 }}
            animate={{ x: 0 }}
            transition={{ type: "spring", stiffness: 120 }}
          />
          <motion.input
            type="text"
            placeholder="Enter Captcha: 1234"
            className="input-field"
            value={captcha}
            onChange={(e) => setCaptcha(e.target.value)}
            initial={{ x: 200 }}
            animate={{ x: 0 }}
            transition={{ type: "spring", stiffness: 120 }}
          />

          <motion.button
            type="submit"
            className="cta-button"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 400 }}
          >
            Login
          </motion.button>
        </form>

        {message && <p className="message">{message}</p>}

        <motion.p
          className="signup-link"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1 }}
        >
          Don't have an account?{" "}
          <span
            className="signup-btn"
            onClick={() => navigate("/signup")}
          >
            Sign Up
          </span>
        </motion.p>
      </motion.div>
    </div>
  );
}

export default Login;
