import React from "react";
import { motion } from "framer-motion";
import "./AboutUs.css";

function AboutUs() {
  return (
    <div className="about-container">
      <motion.div
        className="hero-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <motion.h1
          className="headline"
          initial={{ y: -100 }}
          animate={{ y: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          About Signify
        </motion.h1>
        <motion.p
          className="subheadline"
          initial={{ x: 200 }}
          animate={{ x: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Advanced Signature Verification for Secure Transactions
        </motion.p>
      </motion.div>

      <motion.div
        className="content-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5, delay: 0.5 }}
      >
        <h2>Our Vision</h2>
        <p>
          This is a web-based application that verifies cheque signatures
          against stored signatures using Deep Learning for enhanced security
          and accuracy. The system leverages pre-trained deep learning models,
          fine-tuned for the task of signature verification, to ensure high
          accuracy and robustness.
        </p>

        <h2>How It Works</h2>
        <p>
          Users can upload cheque images for signature verification. The system
          will preprocess the cheque image to extract the signature. The
          extracted signature will be compared against stored signatures using
          a deep learning model, leveraging pre-trained architectures like
          VGG16 or ResNet. The system will return a verification result
          indicating whether the signature is genuine or forged.
        </p>
      </motion.div>
    </div>
  );
}

export default AboutUs;
