import React, { useState } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import "./VerifyPage.css"; // Ensure to style your page as per your design

function VerifyPage() {
  const [accountNumber, setAccountNumber] = useState("");
  const [isAccountVerified, setIsAccountVerified] = useState(false);
  const [signatureFile, setSignatureFile] = useState(null);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // Dummy account verification logic
  const handleAccountVerification = () => {
    // Example of a valid account number (you can replace this with an API call)
    if (accountNumber === "123") {
      setIsAccountVerified(true);
      setMessage("Account verified. You can now upload the signature.");
    } else {
      setIsAccountVerified(false);
      setMessage("Invalid account number. Please try again.");
    }
  };

  // Handle signature file upload
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSignatureFile(file);
    }
  };

  // Handle signature verification
  const handleVerifySignature = () => {
    if (!signatureFile) {
      setMessage("Please upload a signature to verify.");
    } else {
      setMessage("Signature uploaded and verification in process...");
      // Add your signature verification logic here
      setTimeout(() => {
        setMessage("Signature verified successfully!");
        // Navigate to another page or take action after verification
      }, 2000);
    }
  };

  return (
    <div className="verify-container">
      <motion.div
        className="hero-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <motion.h1
          className="headline"
          initial={{ y: -100 }}
          animate={{ y: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Verify Signature
        </motion.h1>
        <motion.p
          className="subheadline"
          initial={{ x: 200 }}
          animate={{ x: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Enter your account number to unlock signature verification
        </motion.p>

        <div className="account-number-section">
          <input
            type="text"
            placeholder="Enter Account Number"
            className="input-field"
            value={accountNumber}
            onChange={(e) => setAccountNumber(e.target.value)}
          />
          <motion.button
            className="cta-button"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 400 }}
            onClick={handleAccountVerification}
          >
            Verify Account
          </motion.button>
        </div>

        {message && <p className="message">{message}</p>}

        {isAccountVerified && (
          <div className="signature-upload-section">
            <h2>Upload Signature</h2>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              disabled={!isAccountVerified}
            />
            {signatureFile && (
              <p>File: {signatureFile.name}</p>
            )}
            <motion.button
              className="cta-button"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              transition={{ type: "spring", stiffness: 400 }}
              onClick={handleVerifySignature}
            >
              Verify Signature
            </motion.button>
          </div>
        )}
      </motion.div>
    </div>
  );
}

export default VerifyPage;
