import React from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import "./Home.css";  // Ensure you have the corresponding CSS file

function Home() {
  const navigate = useNavigate();  // Hook to navigate to other pages

  const handleGetStartedClick = () => {
    // Navigate to the login page
    navigate("/login");
  };

  return (
    <div className="home-container">
      <motion.div
        className="hero-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5 }}
      >
        <motion.h1
          className="headline"
          initial={{ y: -100 }}
          animate={{ y: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Welcome to Signify
        </motion.h1>
        <motion.p
          className="subheadline"
          initial={{ x: 200 }}
          animate={{ x: 0 }}
          transition={{ type: "spring", stiffness: 120 }}
        >
          Advanced Signature Verification for Secure Transactions
        </motion.p>
        <motion.button
          className="cta-button"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          transition={{ type: "spring", stiffness: 400 }}
          onClick={handleGetStartedClick} // Add onClick to handle button click
        >
          Get Started
        </motion.button>
      </motion.div>

      <motion.div
        className="signature-animation"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.5 }}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 200 100"
          className="signature-svg"
        >
          <motion.path
            d="M10 50 Q 50 10, 90 50 T 170 50"
            fill="transparent"
            stroke="white"
            strokeWidth="4"
            strokeLinecap="round"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: 1 }}
            transition={{
              duration: 2,
              ease: "easeInOut",
            }}
          />
        </svg>
      </motion.div>

      {/* Footer */}
      <div className="footer">
        <div className="copyright">
          <span>Â©</span>
          {/* <span className="certified">C</span> */}
          <span> 1996-2024, Signify.com, Inc. or its affiliates</span>
        </div>
      </div>
    </div>
  );
}

export default Home;
