import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignupPage.css'; // Link to external CSS

const SignupPage = () => {
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    username: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState(''); // For handling errors
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { firstName, lastName, username, password } = form;

    try {
      const response = await fetch('http://localhost:5000/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ firstName, lastName, username, password }),
      });

      if (!response.ok) {
        throw new Error('Signup failed! Please try again.');
      }

      const data = await response.json();

      // Check for success message or redirect to verification page
      if (data.message === 'User created') {
        navigate('/verification'); // Redirect to verification page after successful signup
      } else {
        setErrorMessage(data.message || 'An error occurred. Please try again.');
      }
    } catch (error) {
      setErrorMessage(error.message); // Display error message if request fails
    }
  };

  return (
    <div className="signup-container">
      <h1>Create Account</h1>
      {errorMessage && <div className="error-message">{errorMessage}</div>} {/* Display error if exists */}
      <form onSubmit={handleSubmit} className="signup-form">
        <div className="form-group">
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            name="firstName"
            id="firstName"
            value={form.firstName}
            onChange={handleChange}
            className="signup-input"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            name="lastName"
            id="lastName"
            value={form.lastName}
            onChange={handleChange}
            className="signup-input"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            id="username"
            value={form.username}
            onChange={handleChange}
            className="signup-input"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            id="password"
            value={form.password}
            onChange={handleChange}
            className="signup-input"
            required
          />
        </div>
        <button type="submit" className="signup-btn">
          Signup
        </button>
      </form>
    </div>
  );
};

export default SignupPage;
